mapper.update:
UPDATE 
TB_USER A 
INNER JOIN ROLES B
ON A.user_id = B.user_id

SET
  USERNAME = #{username}
, PASSWORD = #{password}
, MANAGER_NAME = #{manager_name}
, MANAGER_TEL = #{manager_tel}
, MANAGER_EMAIL = #{manager_email}
, CAL_MANAGER_NAME = #{cal_manager_name}
, CAL_MANAGER_TEL = #{cal_manager_tel}
, CAL_MANAGER_EMAIL = #{cal_manager_email}
, MODIFYAT = sysdate()
, DISABLE_TIME = #{disable_time}
WHERE A.user_id = #{user_id}
